import React from "react";

const EvaluationResults = ({ results }) => {
  if (!results || results.length === 0) {
    return (
      <div>
        <h2>Değerlendirme Sonuçları</h2>
        <p>Henüz bir değerlendirme yapılmadı.</p>
      </div>
    );
  }

  // En yüksek puanı belirle
  const highestScore = Math.max(...results.map((result) => result.totalScore));

  return (
    <div className="evaluation-results">
      <h2>Değerlendirme Sonuçları</h2>
      <table border="1" style={{ marginTop: "20px", width: "100%" }}>
        <thead>
          <tr>
            <th>Alternatif</th>
            <th>Toplam Skor</th>
          </tr>
        </thead>
        <tbody>
          {results.map((result, index) => (
            <tr
              key={index}
              style={{
                backgroundColor:
                  result.totalScore === highestScore ? "#d4edda" : "white",
              }}
            >
              <td>{result.name}</td>
              <td>{result.totalScore}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EvaluationResults;
