import './App.css';
import InputArea from './templates/InputArea';
import ButtonPropt from './templates/ButtonPropt';
import TableFunc from './templates/TableFunc';
import { useState } from 'react';

function App() {
  const [row, setRow] = useState(1);
  const [column, setColumn] = useState(3);

  return (
    <div className="Field">
      <h1>Kepner-Tregoe Method</h1>
      <div className="First-Buttons-Part">
        <ButtonPropt
          name="Add Parameter"
          bgColor="lightblue"
          onClickFunc={() => setRow(row + 1)}
        />
        <ButtonPropt
          name="Add Alternative"
          bgColor="lightblue"
          onClickFunc={() => setColumn(column + 1)}
        />
      </div>
      <div className="Input-Area">
        <TableFunc rowSize={row} columnSize={column} />
      </div>
      <div className="Second-Buttons-Part">
        <ButtonPropt
          name="Calculate"
          bgColor="yellowgreen"
          onClickFunc={() => console.log("hi!")}
        />
        <ButtonPropt
          name="Reset Form"
          bgColor="yellow"
          color="black"
          onClickFunc={() => {
            setRow(1);
            setColumn(3);
          }}
        />
      </div>
    </div>
  );
}

export default App;
