import React from 'react';
import { VictoryPie, VictoryTheme } from 'victory';

const PieChart = () => {
  // Örnek veri kümesi
  const data = [
    { category: "A", value: 35 },
    { category: "B", value: 40 },
    { category: "C", value: 55 },
    { category: "D", value: 70 }
  ];

  return (
    <VictoryPie
      data={data}
      x="category"
      y="value"
      labelRadius={({ datum }) => 50 + datum.y / 2} // Etiketlerin yarıçapını değere göre ayarla
      style={{
        data: {
          fillOpacity: 0.8,
          stroke: "#000",
          strokeWidth: 2
        },
        labels: { fontSize: 15, fill: "#333" }
      }}
      theme={VictoryTheme.material}
      colorScale={["#c43a31", "#ffcc00", "#00a2ff", "#ffa500"]} // Renk skalası
    />
  );
};

export default PieChart;
