import React, { useState } from "react";
import CriteriaForm from "./templates/CriteriaForm";
import AlternativesForm from "./templates/AlternativesForm";
import EvaluationResults from "./templates/EvaluationResults";

const App = () => {
  const [criteria, setCriteria] = useState([]);
  const [alternatives, setAlternatives] = useState([]);
  const [results, setResults] = useState([]);

  const evaluateAlternatives = () => {
    // Basit bir değerlendirme algoritması.
    const evaluated = alternatives.map((alt) => {
      let score = 0;
      criteria.forEach((criterion) => {
        const value = alt[criterion.name];
        score += criterion.weights[value] || 0; // Ağırlıkları kullan
      });
      return { ...alt, score };
    });
    // Alternatifleri sıralama
    setResults(evaluated.sort((a, b) => b.score - a.score));
  };

  return (
    <div>
      <h1>DEX Karar Destek Sistemi</h1>
      <CriteriaForm setCriteria={setCriteria} />
      <AlternativesForm criteria={criteria} setAlternatives={setAlternatives} />
      <button onClick={evaluateAlternatives}>Alternatifleri Değerlendir</button>
      <EvaluationResults results={results} />
    </div>
  );
};

export default App;
