import './TableFunc.css'


const results = ["",""]

function headerArrayCreator(columnSize){
    const columnHeaders = [];
    for (let i = 0; i < columnSize; i++) {
        if (i === 0)    
            columnHeaders.push("Parameters");
        else if (i === 1)
            columnHeaders.push("Weight (0-10)");
        else
            columnHeaders.push("Alternative " + (i - 1));
    }
    return columnHeaders
}

function headerCreator(columnSize) {
    const columnHeaders = headerArrayCreator(columnSize);
    const columns = [];
    for (let i = 0; i < columnSize; i++) {
        if(i>1)
            columns.push(
                <th className='Header-Type'>
                    {columnHeaders[i]}
                    <input type='text' placeholder="Job" className='Alternative-Inputs'/>
                </th>)
        else
            columns.push(<th className='Header-Type'>{columnHeaders[i]}</th>);
    }

    return (
        <tr>
            {columns}
        </tr>
    );
}

function rowCreator(rowSize,columnSize) {
    const rows = [];
    for (let i = 0; i < rowSize; i++) {
        rows.push(rowGen(i,columnSize));  
    }
    return rows;
}

function rowGen(index, columnSize) {
    const columns = [];
    for (let i = 0; i < columnSize; i++) {
        if(i===0)
            columns.push(<td key={i} className="First-Row-Element">
                <input type='checkbox' className='Row-Inputs checkboxes'/>
                <input type='text' placeholder="Alternative Name" className='Row-Inputs texte'/>
            </td>);
        else if(i===1)   
            columns.push(<td key={i}><input type="number" placeholder="0" className="weigthInputs"></input></td>)
        else   
            columns.push(<td key={i}><input type="number" placeholder="0" className="numberArea"></input></td>)
    }

    return (
        <tr key={index}>
            {columns}
        </tr>
    );
}

function resultRowGen(index,columnSize){
    const columnsForResults = [];
    for(let i = 0 ; i< columnSize;i++){
            columnsForResults.push(<td key={i}>{results[i]}</td>)
    }

    return(
        <tr key={index}>
            {columnsForResults}
        </tr>
    )
}

function TableFunc({rowSize, columnSize}) {

    function valueReader(){
        //For checkboxes
        const checkboxElements = [...document.querySelectorAll('.checkboxes')];
        const checkboxValues = checkboxElements.map(element => element.checked);
        console.log("checkbox values are: "+checkboxValues)

        //For weigths
        const weightElements = [...document.querySelectorAll('.weigthInputs')];
        const weightValues = weightElements.map(input => parseInt(input.value));
        console.log("weight values are: "+weightValues)

        //Fro the all values 
        const inputElements = [...document.querySelectorAll('.numberArea')];
        const inputValues = inputElements.map(input => parseInt(input.value));
        console.log("The all values are: "+inputValues)

        //For job names
        const jobElements = [...document.querySelectorAll('.Alternative-Inputs')];
        const jobValues = jobElements.map(input=>input.value);
        console.log("job names are: "+jobValues)


    }


    return(
        <div className="fiels">
            <table>
                <thead>
                    {headerCreator(columnSize)}
                </thead>
                <tbody>
                    {rowCreator(rowSize,columnSize)}
                    {resultRowGen(rowSize,columnSize)}
                </tbody>
            </table>    
            <button onClick={valueReader}  /> 
        </div>
    );
}

export default TableFunc;
