import React from "react";

const DynamicDEXTable = ({ criteria, setCriteria, alternatives, setAlternatives }) => {
  const addCriterion = () => {
    setCriteria([
      ...criteria,
      { name: "", category: "Medium", impact: "Positive", subCriteria: [] },
    ]);
  };

  const addAlternative = () => {
    setAlternatives([
      ...alternatives,
      {
        name: "",
        ...criteria.reduce((acc, criterion) => {
          acc[criterion.name] = 0;
          return acc;
        }, {}),
      },
    ]);
  };

  const updateAlternative = (index, field, value) => {
    const updatedAlternatives = [...alternatives];
    updatedAlternatives[index][field] = value;
    setAlternatives(updatedAlternatives);
  };

  return (
    <div>
      <h2>Kriterler</h2>
      <button onClick={addCriterion}>Kriter Ekle</button>
      <table>
        <thead>
          <tr>
            <th>Alternatifler</th>
            {criteria.map((criterion, index) => (
              <th key={index}>{criterion.name}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {alternatives.map((alternative, altIndex) => (
            <tr key={altIndex}>
              <td>
                <input
                  type="text"
                  value={alternative.name}
                  onChange={(e) =>
                    updateAlternative(altIndex, "name", e.target.value)
                  }
                />
              </td>
              {criteria.map((criterion, critIndex) => (
                <td key={critIndex}>
                  {criterion.subCriteria.length > 0 ? (
                    <span>-</span> // Alt kriteri varsa input gizlenir
                  ) : (
                    <input
                      type="number"
                      value={alternative[criterion.name] || ""}
                      onChange={(e) =>
                        updateAlternative(
                          altIndex,
                          criterion.name,
                          parseFloat(e.target.value) || 0
                        )
                      }
                    />
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DynamicDEXTable;
