import React, { useState } from "react";

const DynamicDEXTable = ({ criteria, setCriteria, alternatives, setAlternatives }) => {
  const addCriterion = () => {
    setCriteria([
      ...criteria,
      { name: "", category: "Medium", impact: "Positive", subCriteria: [] },
    ]);
  };

  const addAlternative = () => {
    setAlternatives([...alternatives, { name: "", ...criteria.reduce((acc, criterion) => {
      acc[criterion.name] = 0;
      return acc;
    }, {}) }]);
  };

  const updateCriterion = (index, field, value) => {
    const updatedCriteria = [...criteria];
    updatedCriteria[index][field] = value;
    setCriteria(updatedCriteria);
  };

  const addSubCriterion = (index) => {
    const updatedCriteria = [...criteria];
    const parentCriterion = updatedCriteria[index];
    parentCriterion.subCriteria.push({
      name: "",
      category: "Medium",
      impact: "Positive",
      subCriteria: [],
    });
    setCriteria(updatedCriteria);
  };

  const updateAlternative = (index, field, value) => {
    const updatedAlternatives = [...alternatives];
    updatedAlternatives[index][field] = value;
    setAlternatives(updatedAlternatives);
  };

  return (
    <div>
      <h2>Kriterler</h2>
      <button onClick={addCriterion}>Kriter Ekle</button>
      <table>
        <thead>
          <tr>
            <th>Kriter Adı</th>
            <th>Kategori</th>
            <th>Etkisi</th>
            <th>Alt Kriterler</th>
          </tr>
        </thead>
        <tbody>
          {criteria.map((criterion, index) => (
            <React.Fragment key={index}>
              <tr>
                <td>
                  <input
                    type="text"
                    value={criterion.name}
                    onChange={(e) =>
                      updateCriterion(index, "name", e.target.value)
                    }
                  />
                </td>
                <td>
                  <select
                    value={criterion.category}
                    onChange={(e) =>
                      updateCriterion(index, "category", e.target.value)
                    }
                    disabled={criterion.subCriteria.length > 0} // Alt kriter varsa kilitliyoruz
                  >
                    <option value="High">High</option>
                    <option value="Medium">Medium</option>
                    <option value="Low">Low</option>
                  </select>
                </td>
                <td>
                  <select
                    value={criterion.impact}
                    onChange={(e) =>
                      updateCriterion(index, "impact", e.target.value)
                    }
                    disabled={criterion.subCriteria.length > 0} // Alt kriter varsa kilitliyoruz
                  >
                    <option value="Positive">Positive</option>
                    <option value="Negative">Negative</option>
                  </select>
                </td>
                <td>
                  <button onClick={() => addSubCriterion(index)}>
                    Alt Kriter Ekle
                  </button>
                </td>
              </tr>
              {criterion.subCriteria.map((subCriterion, subIndex) => (
                <tr key={`${index}-${subIndex}`} style={{ paddingLeft: "20px" }}>
                  <td>
                    <input
                      type="text"
                      value={subCriterion.name}
                      onChange={(e) =>
                        updateCriterion(index, `subCriteria.${subIndex}.name`, e.target.value)
                      }
                    />
                  </td>
                  <td>
                    <select
                      value={subCriterion.category}
                      onChange={(e) =>
                        updateCriterion(
                          index,
                          `subCriteria.${subIndex}.category`,
                          e.target.value
                        )
                      }
                    >
                      <option value="High">High</option>
                      <option value="Medium">Medium</option>
                      <option value="Low">Low</option>
                    </select>
                  </td>
                  <td>
                    <select
                      value={subCriterion.impact}
                      onChange={(e) =>
                        updateCriterion(
                          index,
                          `subCriteria.${subIndex}.impact`,
                          e.target.value
                        )
                      }
                    >
                      <option value="Positive">Positive</option>
                      <option value="Negative">Negative</option>
                    </select>
                  </td>
                  <td></td>
                </tr>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>

      <h2>Alternatifler</h2>
      <button onClick={addAlternative}>Alternatif Ekle</button>
      <table>
        <thead>
          <tr>
            <th>Alternatif Adı</th>
            {criteria.map((criterion, index) => (
              <th key={index}>{criterion.name}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {alternatives.map((alternative, index) => (
            <tr key={index}>
              <td>
                <input
                  type="text"
                  value={alternative.name}
                  onChange={(e) =>
                    updateAlternative(index, "name", e.target.value)
                  }
                />
              </td>
              {criteria.map((criterion, critIndex) => (
                <td key={critIndex}>
                  {criterion.subCriteria.length > 0 ? null : (
                    <input
                      type="number"
                      value={alternative[criterion.name] || ""}
                      onChange={(e) =>
                        updateAlternative(
                          index,
                          criterion.name,
                          parseFloat(e.target.value) || 0
                        )
                      }
                    />
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DynamicDEXTable;
