import React, { useState } from "react";

const DynamicDEXTable = () => {
  const [criteria, setCriteria] = useState([]);
  const [alternatives, setAlternatives] = useState([]);
  const [newCriterion, setNewCriterion] = useState("");
  const [newCategory, setNewCategory] = useState("Medium");
  const [newImpact, setNewImpact] = useState("Positive");
  const [selectedParentCriterion, setSelectedParentCriterion] = useState(null);
  const [newAlternative, setNewAlternative] = useState("");

  // Kriter Ekleme
  const addCriterion = () => {
    if (newCriterion.trim() !== "") {
      setCriteria([
        ...criteria,
        { name: newCriterion, category: newCategory, impact: newImpact, subCriteria: [] },
      ]);
      setNewCriterion("");
      setNewCategory("Medium");
      setNewImpact("Positive");
    }
  };

  // Alt Kriter Ekleme
  const addSubCriterion = (parentCriterion, subCriterionName, category, impact) => {
    const updateCriteria = (list) =>
      list.map((criterion) => {
        if (criterion === parentCriterion) {
          return {
            ...criterion,
            subCriteria: [
              ...criterion.subCriteria,
              { name: subCriterionName, category, impact, subCriteria: [] },
            ],
          };
        }
        if (criterion.subCriteria.length > 0) {
          return { ...criterion, subCriteria: updateCriteria(criterion.subCriteria) };
        }
        return criterion;
      });

    setCriteria(updateCriteria(criteria));
  };

  // Alternatif Ekleme
  const addAlternative = () => {
    if (newAlternative.trim() !== "") {
      const newAlt = { name: newAlternative };

      // Tüm kriterlere uygun boş input alanları oluştur
      const initializeInputs = (criterionList) => {
        criterionList.forEach((criterion) => {
          newAlt[criterion.name] = "";
          if (criterion.subCriteria.length > 0) {
            initializeInputs(criterion.subCriteria);
          }
        });
      };

      initializeInputs(criteria);
      setAlternatives([...alternatives, newAlt]);
      setNewAlternative("");
    }
  };

  // Alternatif Güncelleme
  const updateAlternative = (index, criterionName, value) => {
    const updatedAlternatives = [...alternatives];
    updatedAlternatives[index] = {
      ...updatedAlternatives[index],
      [criterionName]: value,
    };
    setAlternatives(updatedAlternatives);
  };

  // Hiyerarşik Tablo Başlıkları
  const renderHeaders = (criterionList, depth = 0) =>
    criterionList.flatMap((criterion) => [
      <th
        key={criterion.name}
        colSpan={criterion.subCriteria.length > 0 ? criterion.subCriteria.length : 1}
        style={{ paddingLeft: `${depth * 20}px` }}
      >
        {criterion.name} ({criterion.category}, {criterion.impact})
      </th>,
      ...(criterion.subCriteria.length > 0
        ? renderHeaders(criterion.subCriteria, depth + 1)
        : []),
    ]);

  // Hiyerarşik Değerler
  const renderInputs = (criterionList, alternative, rowIndex) =>
    criterionList.flatMap((criterion) => [
      <td key={`${alternative.name}-${criterion.name}`}>
        <input
          type="number"
          placeholder={criterion.name}
          value={alternative[criterion.name] || ""}
          onChange={(e) =>
            updateAlternative(rowIndex, criterion.name, e.target.value)
          }
        />
      </td>,
      ...(criterion.subCriteria.length > 0
        ? renderInputs(criterion.subCriteria, alternative, rowIndex)
        : []),
    ]);

  return (
    <div>
      <h2>Kriterler ve Alternatifler</h2>

      {/* Kriter Ekleme */}
      <div>
        <input
          type="text"
          placeholder="Kriter Adı"
          value={newCriterion}
          onChange={(e) => setNewCriterion(e.target.value)}
        />
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        <select
          value={newImpact}
          onChange={(e) => setNewImpact(e.target.value)}
        >
          <option value="Positive">Pozitif</option>
          <option value="Negative">Negatif</option>
        </select>
        <button onClick={addCriterion}>Kriter Ekle</button>
      </div>

      {/* Alt Kriter Ekleme */}
      <div>
        <select
          value={selectedParentCriterion || ""}
          onChange={(e) =>
            setSelectedParentCriterion(
              criteria.find((c) => c.name === e.target.value) || null
            )
          }
        >
          <option value="">Ana Kriter Seç</option>
          {criteria.map((criterion) => (
            <option key={criterion.name} value={criterion.name}>
              {criterion.name}
            </option>
          ))}
        </select>
        {selectedParentCriterion && (
          <>
            <input
              type="text"
              placeholder="Alt Kriter Adı"
              value={newCriterion}
              onChange={(e) => setNewCriterion(e.target.value)}
            />
            <select
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
            >
              <option value="High">High</option>
              <option value="Medium">Medium</option>
              <option value="Low">Low</option>
            </select>
            <select
              value={newImpact}
              onChange={(e) => setNewImpact(e.target.value)}
            >
              <option value="Positive">Pozitif</option>
              <option value="Negative">Negatif</option>
            </select>
            <button
              onClick={() => {
                addSubCriterion(
                  selectedParentCriterion,
                  newCriterion,
                  newCategory,
                  newImpact
                );
                setNewCriterion("");
              }}
            >
              Alt Kriter Ekle
            </button>
          </>
        )}
      </div>

      {/* Alternatif Ekleme */}
      <div>
        <input
          type="text"
          placeholder="Alternatif Adı"
          value={newAlternative}
          onChange={(e) => setNewAlternative(e.target.value)}
        />
        <button onClick={addAlternative}>Alternatif Ekle</button>
      </div>

      {/* Dinamik Tablo */}
      <table border="1" style={{ marginTop: "20px", width: "100%" }}>
        <thead>
          <tr>
            <th>Alternatifler</th>
            {renderHeaders(criteria)}
          </tr>
        </thead>
        <tbody>
          {alternatives.map((alt, rowIndex) => (
            <tr key={rowIndex}>
              <td>{alt.name}</td>
              {renderInputs(criteria, alt, rowIndex)}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DynamicDEXTable;
