import React from 'react';
import { VictoryPie, VictoryTheme } from 'victory';
import './Charts.css'


function Chart2({numberArray, parameterNames, resultList}){

    const convertedResults = resultList.map(item => (typeof item === 'number' ? item : 0));
    let max =convertedResults[0]
    let maxIndex = 0
    for(let i=1;i<convertedResults.length;i++){
        if(max<convertedResults[i]){
            max=convertedResults[i]
            maxIndex=i;
        }
    }

    const yValues = [10,10,10,10,10,10,10,10,20];
    //console.log("result list: "+resultList+" maxValue is: "+ max+" and index of max values is: "+maxIndex)
    console.log(resultList.length)
    var numList = []
    for(let i = 0 ; i < parameterNames.length ; i++){
        if(i===0)
            numList.push(numberArray[maxIndex-2])
        else
            numList.push((i*resultList.length)+(maxIndex-2))
    }
    console.log(numList)


    const data = parameterNames.map((names,index)=>({
        x:names,
        y:numberArray[index]
    }))

    return (
        <div className='chart'>
            <VictoryPie
            radius={({ datum }) => datum.y + 75}
            data={data}
            theme={VictoryTheme.clean}
            />
        </div>
    );
}

export default Chart2;

