import React, { useState } from "react";

const DynamicDEXTable = () => {
  const [criteria, setCriteria] = useState([]);
  const [alternatives, setAlternatives] = useState([]);
  const [newCriterion, setNewCriterion] = useState("");
  const [newCategory, setNewCategory] = useState("Medium");
  const [newImpact, setNewImpact] = useState("Positive");
  const [selectedParentCriterion, setSelectedParentCriterion] = useState(null);
  const [newAlternative, setNewAlternative] = useState("");

  // Yeni kriter ekleme
  const addCriterion = () => {
    if (newCriterion.trim() !== "") {
      setCriteria([
        ...criteria,
        {
          id: Date.now(),
          name: newCriterion,
          category: newCategory,
          impact: newImpact,
          subCriteria: [],
        },
      ]);
      setNewCriterion("");
    }
  };

  // Alt kriter ekleme
  const addSubCriterion = () => {
    if (newCriterion.trim() !== "" && selectedParentCriterion) {
      const updatedCriteria = criteria.map((criterion) =>
        criterion.id === parseInt(selectedParentCriterion)
          ? {
              ...criterion,
              subCriteria: [
                ...criterion.subCriteria,
                {
                  id: Date.now(),
                  name: newCriterion,
                  category: newCategory,
                  impact: newImpact,
                  subCriteria: [],
                },
              ],
            }
          : {
              ...criterion,
              subCriteria: addSubCriterionToList(
                criterion.subCriteria,
                parseInt(selectedParentCriterion)
              ),
            }
      );
      setCriteria(updatedCriteria);
      setNewCriterion("");
      setSelectedParentCriterion(null);
    }
  };

  const addSubCriterionToList = (list, parentId) =>
    list.map((subCriterion) =>
      subCriterion.id === parentId
        ? {
            ...subCriterion,
            subCriteria: [
              ...subCriterion.subCriteria,
              {
                id: Date.now(),
                name: newCriterion,
                category: newCategory,
                impact: newImpact,
                subCriteria: [],
              },
            ],
          }
        : {
            ...subCriterion,
            subCriteria: addSubCriterionToList(
              subCriterion.subCriteria,
              parentId
            ),
          }
    );

  // Alternatif ekleme
  const addAlternative = () => {
    if (newAlternative.trim() !== "") {
      const newAlt = { id: Date.now(), name: newAlternative, values: {} };
      setAlternatives([...alternatives, newAlt]);
      setNewAlternative("");
    }
  };

  // Alternatif güncelleme
  const updateAlternative = (alternativeId, criterionId, value) => {
    const updatedAlternatives = alternatives.map((alternative) =>
      alternative.id === alternativeId
        ? {
            ...alternative,
            values: {
              ...alternative.values,
              [criterionId]: value,
            },
          }
        : alternative
    );
    setAlternatives(updatedAlternatives);
  };

  // Kriterleri ve alt kriterleri kolon başlıkları olarak render etme
  const renderHeaders = (criteriaList, depth = 0) =>
    criteriaList.map((criterion) => (
      <React.Fragment key={criterion.id}>
        <th style={{ paddingLeft: `${depth * 10}px` }}>
          {criterion.name} ({criterion.category}, {criterion.impact})
        </th>
        {criterion.subCriteria.length > 0 &&
          renderHeaders(criterion.subCriteria, depth + 1)}
      </React.Fragment>
    ));

  // Alternatiflerin input alanlarını render etme
  const renderInputs = (criteriaList, alternative, depth = 0) =>
    criteriaList.map((criterion) => (
      <React.Fragment key={criterion.id}>
        <td>
          <input
            type="number"
            placeholder={criterion.name}
            value={alternative.values[criterion.id] || ""}
            onChange={(e) =>
              updateAlternative(alternative.id, criterion.id, e.target.value)
            }
          />
        </td>
        {criterion.subCriteria.length > 0 &&
          renderInputs(criterion.subCriteria, alternative, depth + 1)}
      </React.Fragment>
    ));

  return (
    <div>
      <h2>Kriterler ve Alternatifler</h2>

      {/* Yeni kriter ekleme */}
      <div>
        <input
          type="text"
          placeholder="Kriter Adı"
          value={newCriterion}
          onChange={(e) => setNewCriterion(e.target.value)}
        />
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        <select
          value={newImpact}
          onChange={(e) => setNewImpact(e.target.value)}
        >
          <option value="Positive">Pozitif</option>
          <option value="Negative">Negatif</option>
        </select>
        <button onClick={addCriterion}>Kriter Ekle</button>
      </div>

      {/* Alt kriter ekleme */}
      <div>
        <select
          value={selectedParentCriterion || ""}
          onChange={(e) => setSelectedParentCriterion(e.target.value)}
        >
          <option value="">Ana Kriter Seç</option>
          {criteria.map((criterion) => (
            <option key={criterion.id} value={criterion.id}>
              {criterion.name}
            </option>
          ))}
        </select>
        {selectedParentCriterion && (
          <>
            <input
              type="text"
              placeholder="Alt Kriter Adı"
              value={newCriterion}
              onChange={(e) => setNewCriterion(e.target.value)}
            />
            <select
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
            >
              <option value="High">High</option>
              <option value="Medium">Medium</option>
              <option value="Low">Low</option>
            </select>
            <select
              value={newImpact}
              onChange={(e) => setNewImpact(e.target.value)}
            >
              <option value="Positive">Pozitif</option>
              <option value="Negative">Negatif</option>
            </select>
            <button onClick={addSubCriterion}>Alt Kriter Ekle</button>
          </>
        )}
      </div>

      {/* Alternatif ekleme */}
      <div>
        <input
          type="text"
          placeholder="Alternatif Adı"
          value={newAlternative}
          onChange={(e) => setNewAlternative(e.target.value)}
        />
        <button onClick={addAlternative}>Alternatif Ekle</button>
      </div>

      {/* Dinamik Tablo */}
      <table border="1" style={{ marginTop: "20px", width: "100%" }}>
        <thead>
          <tr>
            <th>Alternatifler</th>
            {renderHeaders(criteria)}
          </tr>
        </thead>
        <tbody>
          {alternatives.map((alt) => (
            <tr key={alt.id}>
              <td>{alt.name}</td>
              {renderInputs(criteria, alt)}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DynamicDEXTable;
