import React, { useState } from "react";

const DynamicDEXTable = ({ criteria, setCriteria, alternatives, setAlternatives }) => {
  const [newCriterion, setNewCriterion] = useState("");
  const [newCategory, setNewCategory] = useState("Medium");
  const [newImpact, setNewImpact] = useState("Positive");
  const [selectedCriterion, setSelectedCriterion] = useState("");
  const [newSubCriterion, setNewSubCriterion] = useState("");
  const [newAlternative, setNewAlternative] = useState({ name: "" });

  // Yeni kriter ekleme
  const addCriterion = () => {
    if (newCriterion.trim() !== "") {
      setCriteria([
        ...criteria,
        { name: newCriterion, category: newCategory, impact: newImpact, subCriteria: [] },
      ]);
      setNewCriterion("");
      setNewCategory("Medium");
      setNewImpact("Positive");
    }
  };

  // Yeni alt kriter ekleme
  const addSubCriterion = () => {
    if (selectedCriterion && newSubCriterion.trim() !== "") {
      const updatedCriteria = criteria.map((criterion) => {
        if (criterion.name === selectedCriterion) {
          return {
            ...criterion,
            subCriteria: [
              ...(criterion.subCriteria || []),
              {
                name: newSubCriterion,
                category: newCategory,
                impact: newImpact,
              },
            ],
          };
        }
        return criterion;
      });

      setCriteria(updatedCriteria);
      setNewSubCriterion("");
      setNewCategory("Medium");
      setNewImpact("Positive");
    }
  };

  // Yeni alternatif ekleme
  const addAlternative = () => {
    if (newAlternative.name.trim() !== "") {
      const newAlt = { name: newAlternative.name };

      // Ana kriterler ve alt kriterlere uygun boş input alanları oluştur
      criteria.forEach((criterion) => {
        if (!criterion.subCriteria.length) {
          newAlt[criterion.name] = "";
        } else {
          (criterion.subCriteria || []).forEach((subCriterion) => {
            newAlt[subCriterion.name] = "";
          });
        }
      });

      setAlternatives([...alternatives, newAlt]);
      setNewAlternative({ name: "" });
    }
  };

  // Alternatif güncelleme
  const updateAlternative = (index, criterionName, value) => {
    const updatedAlternatives = [...alternatives];
    updatedAlternatives[index] = {
      ...updatedAlternatives[index],
      [criterionName]: value,
    };
    setAlternatives(updatedAlternatives);
  };

  return (
    <div>
      <h2>Kriterler ve Alternatifler</h2>

      {/* Kriter Ekleme */}
      <div>
        <input
          type="text"
          placeholder="Kriter Adı"
          value={newCriterion}
          onChange={(e) => setNewCriterion(e.target.value)}
        />
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        <select
          value={newImpact}
          onChange={(e) => setNewImpact(e.target.value)}
        >
          <option value="Positive">Pozitif</option>
          <option value="Negative">Negatif</option>
        </select>
        <button onClick={addCriterion}>Kriter Ekle</button>
      </div>

      {/* Alt Kriter Ekleme */}
      <div>
        <select
          value={selectedCriterion}
          onChange={(e) => setSelectedCriterion(e.target.value)}
        >
          <option value="">Ana Kriter Seç</option>
          {criteria.map((criterion, index) => (
            <option key={index} value={criterion.name}>
              {criterion.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Alt Kriter Adı"
          value={newSubCriterion}
          onChange={(e) => setNewSubCriterion(e.target.value)}
        />
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        <select
          value={newImpact}
          onChange={(e) => setNewImpact(e.target.value)}
        >
          <option value="Positive">Pozitif</option>
          <option value="Negative">Negatif</option>
        </select>
        <button onClick={addSubCriterion}>Alt Kriter Ekle</button>
      </div>

      {/* Alternatif Ekleme */}
      <div>
        <input
          type="text"
          placeholder="Alternatif Adı"
          value={newAlternative.name}
          onChange={(e) =>
            setNewAlternative({ ...newAlternative, name: e.target.value })
          }
        />
        <button onClick={addAlternative}>Alternatif Ekle</button>
      </div>

      {/* Dinamik Tablo */}
      <table border="1" style={{ marginTop: "20px", width: "100%" }}>
        <thead>
          <tr>
            <th>Alternatifler</th>
            {criteria.flatMap((criterion) =>
              criterion.subCriteria.length
                ? criterion.subCriteria.map((subCriterion) => (
                    <th key={subCriterion.name}>
                      {subCriterion.name} ({subCriterion.category},{" "}
                      {subCriterion.impact})
                    </th>
                  ))
                : [
                    <th key={criterion.name}>
                      {criterion.name} ({criterion.category},{" "}
                      {criterion.impact})
                    </th>,
                  ]
            )}
          </tr>
        </thead>
        <tbody>
          {alternatives.map((alt, rowIndex) => (
            <tr key={rowIndex}>
              <td>{alt.name}</td>
              {criteria.flatMap((criterion) =>
                criterion.subCriteria.length
                  ? criterion.subCriteria.map((subCriterion) => (
                      <td key={subCriterion.name}>
                        <input
                          type="number"
                          placeholder={`Alt: ${subCriterion.name}`}
                          value={alt[subCriterion.name] || ""}
                          onChange={(e) =>
                            updateAlternative(
                              rowIndex,
                              subCriterion.name,
                              e.target.value
                            )
                          }
                        />
                      </td>
                    ))
                  : [
                      <td key={criterion.name}>
                        <input
                          type="number"
                          placeholder={`Ana: ${criterion.name}`}
                          value={alt[criterion.name] || ""}
                          onChange={(e) =>
                            updateAlternative(
                              rowIndex,
                              criterion.name,
                              e.target.value
                            )
                          }
                        />
                      </td>,
                    ]
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DynamicDEXTable;
