import './App.css';
import InputArea from './templates/InputArea';
import ButtonPropt from './templates/ButtonPropt';
import TableFunc from './templates/TableFunc';

let row = 1
let column = 3

function App() {
  return (
    <div className="Field">
      <h1>Kepner-Tregoe Method</h1>
      <div className="First-Buttons-Part">
        <ButtonPropt name="Add Parameter" bgColor="lightblue" onClickFunc={()=>{row++}}/>
        <ButtonPropt name="Add Alternative" bgColor="lightblue" onClickFunc={()=>{column++}}/>

      </div>
      <div className="Input-Area">
        <TableFunc rowSize={Number(row)} columnSize={Number(column)}/>
      </div>
      <div className="Second-Buttons-Part">
        <ButtonPropt name="Calculate" bgColor="yellowgreen" onClickFunc={()=>{console.log("hi!")}}/>
        <ButtonPropt name="Reset Form" bgColor="yellow" color="black" onClickFunc={()=>{row=1;column=3}}/>
      </div>
    </div>
  );
}

export default App;
